import { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';

const AppFooter = () => {

    const[TopBtn,setTopBtn]=useState(false);

    useEffect(()=>{
        window.addEventListener('scroll',()=>{
            if(window.scrollY>400){
                setTopBtn(true);
            }else{
                setTopBtn(true);
            }
        })
    },[])
    cn
  return (
    <Container fluid>
        <div className='copyright'>&copy;2024 Corporate. All Right Reserved.
            <div className='socials'>
                <ul>
                <li><a href='https://facebook.com'><i className="fab fa-facebook-f"></i></a></li>
                <li><a href='https://twitter.com'><i className="fab fa-twitter"></i></a></li>
                <li><a href='https://linkedin.com'><i className="fab fa-linkedin-in"></i></a></li>

                </ul>
            </div>
        </div>
        {
            TopBtn && (<div className='go-top'onClick={GoTop}></div>)
        }
    </Container>
)
}

export default AppFooter
