import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";

const blogData = [
    {
      id: 1,
      image: require('../assets/images/blog1.jpg'),
      time: '15 Nov 2016',
      title: 'Coffee Lovers',
      description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Deserunt, asperiores eaque quibusdam eum quod cum nesciunt.',
      link: 'https://www.google.com'
    },
    {
      id: 2,
      image: require('../assets/images/blog2.jpg'),
      time: '10 Nov 2016',
      title: 'Tips for UI Design',
      description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Deserunt, asperiores eaque quibusdam eum quod cum nesciunt.',
      link: 'https://www.facebook.com'
    },
    {
      id: 3,
      image: require('../assets/images/blog3.jpg'),
      time: '07 Nov 2016',
      title: 'Beautiful Day',
      description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Deserunt, asperiores eaque quibusdam eum quod cum nesciunt.',
      link: 'https://www.twitter.com'
    }
  ]

const AppBlog = () => {
  return (
    <section id="blog" className="block blog-block">
      <Container fluid>
        <div className="title-holder">
          <h2>LATEST FROM BLOG</h2>
          <div className="subtitle">get our latest news from blog</div>
        </div>
        <Row>
            {
                blogData.map(blog=>{
                    return(
                        <Col sm={4} key={blog.id}>
                        <div className="holder">
                          <Card >
                            <Card.Img variant="top" src={blog.i} />
                            <Card.Body>
                                <time>{blog.time}</time>
                              <Card.Title>Card Title</Card.Title>
                              <Card.Text>
                                Some quick example text to build on the card title and make
                                up the bulk of the card's content.
                              </Card.Text>
                                <a href="#" className="btn btn-primary">Read More <i class="fas fa-chevron-right"></i></a>
                            </Card.Body>
                          </Card>
                        </div>
                      </Col>
                    )
                })
            }

        </Row>
      </Container>
    </section>
  );
};

export default AppBlog;
